queries.query:
MATCH (oc:esco__Occupation) -- (o:esco__Skill)-[r:dct__description]-(s {esco__language: "en"}) where oc.skos__prefLabel in [ 'actor/actress', 'administrative assistant', 'immigration officer', 'steward/stewardess', 'residential care home worker', 'chief executive officer', 'civil engineer', 'building', 'construction worker', 'electrician', 'emergency medical dispatcher', 'mixed farmer', 'member of parliament', 'executive assistant', 'police officer', 'postman/postwoman', 'primary', 'school teacher', 'sales assistant', 'social worker', 'train driver', 'waiter/waitress']
return { ContentType: 'Skill', TitlePart:{Title:o.skos__prefLabel}
,GraphSyncPart:{Text:o.uri},
Skill:{Description:{Html:s.esco__nodeLiteral},AlternativeLabels:{ContentItemIds:[(o)-[:hasAltLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("SkillLabel", "'+l.skos__prefLabel+'")]']},PreferredLabel:{ContentItemIds:[(o)-[:hasPrefLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("SkillLabel", "'+l.skos__prefLabel+'")]']}}
}
order by o.skos__prefLabel
skip 2400 limit 400
